import { useState } from "react";

const arr = ["cricket", "football", "play"];
export const Practice = () => {
  const [item, setItem] = useState(arr);
  const [ check , setCheck] = useState(new Array(arr.length).fill(true));

  const handlebtn = (itemIndex) => {
    let newarr = item;
    let filterArr = newarr.filter((curr, index) => {
      return index !== itemIndex;
    });
    setItem(filterArr);


    //
    const updatedCheckbox =  check.filter((curr , index) => index!==item)
    setCheck(updatedCheckbox);
    console.log(updatedCheckbox); 
  };

  const handlecheckBox =(index)=>{
    console.log("checked the button");
    const updatedCheckbox = [...check]
    updatedCheckbox[index] = !updatedCheckbox[index];
    setCheck(updatedCheckbox);

  }
  return (
    <div className="app">
      <ul>
        {item.map((currelm, index) => {
          return (
            <li key={index}>
              <input   type="checkbox"   checked={check[index]} onChange={ () => handlecheckBox (index)}/>
              {currelm}
              {/* <button onClick={() => handlebtn(index)}> dtl item</button> */}
              {check[index] && (
              <button onClick={() => handlebtn(index)}>Delete item</button>
            )}
            </li>
          );
        })}
      </ul>
    </div>
  );
};
