import { useState } from "react";

const arr = ["cricket", "football", "play"];
export const Practice = () => {

    const [ item , setItem ] = useState(arr);

    const handlebtn=(itemIndex)=>{
    //   console.log( setItem)
    let newarr = item;
   let filterArr = newarr.filter((curr , index ) =>{
        return  index == itemIndex;
   });
    setItem(filterArr);
    }
  return (
    <div className="app">
      <ul>
        {item.map((currelm , index) => {
          return <li key={index}>
          {/* <input   type=""/> */}
          {currelm}
          <button  onClick={handlebtn(index)}> dtl item</button>
          </li>;

        })}
      </ul>
    </div>
  );
};
