import { useState } from "react";
import { Child } from "./Child";
import "../index.css"

export const Parent = () => {
  const [UIcolor, setUIcolor] = useState(null);

  //our callback
  const getColor = (color) => {
    setUIcolor(color);
  };

  return (
    <>
      <h1> Home page</h1>
      <div className="big-box">
        <div className="small-box" style={{ background: UIcolor }}>
        
        </div>

        <Child getColor={getColor} />
      </div>
    </>
  );
};
