import { useState } from 'react';
import { NavLink } from "react-router-dom";
import { Box, Typography, Button, IconButton, Drawer, List, ListItem } from "@mui/material";
import MenuIcon from '@mui/icons-material/Menu';

export const Header = () => {
  const [drawerOpen, setDrawerOpen] = useState(false);

  const toggleDrawer = (open) => () => {
    setDrawerOpen(open);
  };

  return (
    <Box component="header" sx={{ backgroundColor: '#111', py: 2 }}>
      {/* Navbar Section */}
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          px: 2,
        }}
      >
        {/* Brand Section */}
        <Typography variant="h5" component={NavLink} to="/" sx={{ textDecoration: 'none', color: '#fff' }}>
          Frontend Bootcamp
        </Typography>

        {/* Desktop Navigation Links */}
        <Box sx={{ display: { xs: 'none', md: 'block' } }}>
          <Button
            component={NavLink}
            to="/"
            sx={({ isActive }) => ({
              color: isActive ? 'red' : '#fff', // Red when active, white otherwise
              textDecoration: 'none',
            })}
          >
            Home
          </Button>
          <Button
            component={NavLink}
            to="/about"
            sx={({ isActive }) => ({
              color: isActive ? 'red' : '#fff',
              textDecoration: 'none',
            })}
          >
            About
          </Button>
          <Button
            component={NavLink}
            to="/movie"
            sx={({ isActive }) => ({
              color: isActive ? 'red' : '#fff',
              textDecoration: 'none',
            })}
          >
            Movie
          </Button>
          <Button
            component={NavLink}
            to="/contact"
            sx={({ isActive }) => ({
              color: isActive ? 'red' : '#fff',
              textDecoration: 'none',
            })}
          >
            Contact
          </Button>
        </Box>

        {/* Mobile Menu Icon */}
        <IconButton
          sx={{ display: { xs: 'block', md: 'none' }, color: '#fff' }}
          onClick={toggleDrawer(true)}
        >
          <MenuIcon />
        </IconButton>

        {/* Drawer for mobile menu */}
        <Drawer
          anchor="right"
          open={drawerOpen}
          onClose={toggleDrawer(false)}
          PaperProps={{
            sx: { height: 'auto', minHeight: '50%' }
          }}
        >
          <List sx={{ width: 250 }}>
            <ListItem button component={NavLink} to="/" onClick={toggleDrawer(false)}>
              Home
            </ListItem>
            <ListItem button component={NavLink} to="/about" onClick={toggleDrawer(false)}>
              About
            </ListItem>
            <ListItem button component={NavLink} to="/movie" onClick={toggleDrawer(false)}>
              Movie
            </ListItem>
            <ListItem button component={NavLink} to="/contact" onClick={toggleDrawer(false)}>
              Contact
            </ListItem>
          </List>
        </Drawer>
      </Box>
    </Box>
  );
};
