import { useState } from 'react';
import { NavLink } from "react-router-dom";
import { Box, Typography, Button, IconButton, Drawer, List, ListItem } from "@mui/material";
import MenuIcon from '@mui/icons-material/Menu'; // Import the menu icon

export const Header = () => {
  const [drawerOpen, setDrawerOpen] = useState(false);

  const toggleDrawer = (open) => () => {
    setDrawerOpen(open);
  };

  return (
    <Box component="header" sx={{ backgroundColor: '#111', py: 4, mt:'-1rem' }}>
      {/* Navbar Section */}
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          px: 2,
        }}
      >
        {/* Brand Section */}
        <Typography variant="h5" component={NavLink} to="/" sx={{ textDecoration: 'none', color: '#fff' }}>
          Frontend Bootcamp
        </Typography>

        {/* Desktop Navigation Links */}
        <Box sx={{ display: { xs: 'none', md: 'block' } }}>
          <Button component={NavLink} to="/" sx={{ color: '#fff' }}>Home</Button>
          <Button component={NavLink} to="/about" sx={{ color: '#fff' }}>About</Button>
          <Button component={NavLink} to="/movie" sx={{ color: '#fff' }}>Movie</Button>
          <Button component={NavLink} to="/contact" sx={{ color: '#fff' }}>Contact</Button>
        </Box>

        {/* Mobile Menu Icon */}
        <IconButton
          sx={{ display: { xs: 'block', md: 'none' }, color: '#fff' }}
          onClick={toggleDrawer(true)}
        >
          <MenuIcon />
        </IconButton>

        {/* Drawer for mobile menu */}
        <Drawer anchor="right" open={drawerOpen} onClose={toggleDrawer(false) }
         PaperProps={{
            sx: { height: 'auto', minHeight: '20%'} // Limit height of the drawer
          }}>
          <List sx={{ width: 250  , background:"black"}}>
            <ListItem button component={NavLink} to="/" onClick={toggleDrawer(false)} sx={{color:'#fff'}}>
              Home
            </ListItem>
            <ListItem button component={NavLink} to="/about" onClick={toggleDrawer(false)} sx={{color:'#fff'}}>
              About
            </ListItem>
            <ListItem button component={NavLink} to="/movie" onClick={toggleDrawer(false)} sx={{color:'#fff'}}>
              Movie
            </ListItem>
            <ListItem button component={NavLink} to="/contact" onClick={toggleDrawer(false)}>
              Contact
            </ListItem>
          </List>
        </Drawer>
      </Box>
    </Box>
  );
};
