import { useState } from 'react';
import { NavLink } from "react-router-dom";
import { Box, Typography, Button, IconButton, Drawer, List, ListItem } from "@mui/material";
import MenuIcon from '@mui/icons-material/Menu'; // Import the menu icon

export const Header = () => {
  const [drawerOpen, setDrawerOpen] = useState(false);

  const toggleDrawer = (open) => () => {
    setDrawerOpen(open);
  };

  return (
    <Box component="header" sx={{ backgroundColor: '#111', py: 4, mt:'-1rem' }}>
      {/* Navbar Section */}
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          px: 2,
        }}
      >
        {/* Brand Section */}
        <Typography variant="h5" component={NavLink} to="/" sx={{ textDecoration: 'none', color: '#fff' }}>
          Frontend Bootcamp
        </Typography>

        {/* Desktop Navigation Links */}
        <Box sx={{ display: { xs: 'none', md: 'block' } }}>
          <NavLink to="/" style={({ isActive }) => ({
            textDecoration: 'none',
            color: isActive ? 'red' : '#fff',
            margin: '0 8px', // Adjust spacing if needed
            fontWeight: isActive ? 'bold' : 'normal',
          })}>
            Home
          </NavLink>
          <NavLink to="/about" style={({ isActive }) => ({
            textDecoration: 'none',
            color: isActive ? 'red' : '#fff',
            margin: '0 8px', // Adjust spacing if needed
            fontWeight: isActive ? 'bold' : 'normal',
          })}>
            About
          </NavLink>
          <NavLink to="/movie" style={({ isActive }) => ({
            textDecoration: 'none',
            color: isActive ? 'red' : '#fff',
            margin: '0 8px', // Adjust spacing if needed
            fontWeight: isActive ? 'bold' : 'normal',
          })}>
            Movie
          </NavLink>
          <NavLink to="/contact" style={({ isActive }) => ({
            textDecoration: 'none',
            color: isActive ? 'red' : '#fff',
            margin: '0 8px', // Adjust spacing if needed
            fontWeight: isActive ? 'bold' : 'normal',
          })}>
            Contact
          </NavLink>
        </Box>

        {/* Mobile Menu Icon */}
        <IconButton
          sx={{ display: { xs: 'block', md: 'none' }, color: '#fff' }}
          onClick={toggleDrawer(true)}
        >
          <MenuIcon />
        </IconButton>

        {/* Drawer for mobile menu */}
       
      </Box>
    </Box>
  );
};
