    <Box
      sx={{
        backgroundColor: '#',
        borderRadius: 2,
        boxShadow: 3,
        p: 3,
        textAlign: 'center',
        flex: 1, // Allow cards to grow equally
        minWidth: 0, // Prevent shrinking
      }}
    >
      <Box sx={{ fontSize: '3rem', mb: 2 }}>
        <Icon />
      </Box>
      <Typography variant="h5" gutterBottom>
        {title}
      </Typography>
      <Typography variant="body1" sx={{ mb: 2 }}>
        {text}
      </Typography>
      <Button variant="contained" color={btnColor}>
        Read More
      </Button>
    </Box>

    
  );
};
